import { useParams, Route } from 'react-router-dom'; // importing the Route component
import { Fragment } from 'react';

import Comments from '../components/comments/Comments'; // importing the comments 

const QuoteDetail = (props) => {

    const params = useParams();

    return (
        <Fragment>
            <h1>Quote Details Page</h1>
            <p>{params.quoteId}</p>

            {/* since we want the path to have the same 'quoteId' as the quote whose detailp page 
            is on display, we need to make sure that it's the same quoteId */}

            <Route path={`/quotes/${params.quoteId}/comments`}>
                <Comments />
            </Route>    
        </Fragment>

    );
};

export default QuoteDetail;
