import { useParams, Route, NavLink } from 'react-router-dom'; // importing NavLink 
// component from react-router-dom library
import { Fragment } from 'react';

import HighlightedQuote from '../components/quotes/HighlightedQuote';

import Comments from '../components/comments/Comments'; 

const DUMMY_QUOTES = [ 
    {
        id: 'q1',
        author: 'Kartik',
        text: 'Travelling around the would should be your major goal.'
    },
    {
        id: 'q2',
        author: 'Radhika',
        text: 'I am into adventure sports.'
    },
    {
        id: 'q3',
        author: 'Akshat',
        text: 'You should play on probabilities even if it wastes your time.'
    },
    {
        id: 'q4',
        author: 'Ayushi',
        text: 'Mujhse nhi ho payega ye sab, ho gya toh bhi I will ruin everything!'
    },
    {
        id: 'q5',
        author: 'Priyal',
        text: 'Pagal hai utkarsh, I know he wont text me saamne se!'
    }
];

const QuoteDetail = (props) => {

    const params = useParams();

    const data = DUMMY_QUOTES.find(quote => quote.id === params.quoteId); 

    if(!data){
        return <p>No Quote Found!</p>
    }

    return (
        <Fragment>
            <HighlightedQuote 
                text = {data.text}
                author = {data.author}
            />

            <div className='centered'>
                <NavLink className='btn--flat' to={`/quotes/${params.quoteId}/comments`}>
                    <p>Comment Section</p>
                </NavLink>
            </div>


            <Route path={`/quotes/${params.quoteId}/comments`}>
                <Comments />
            </Route>    
        </Fragment>

    );
};

export default QuoteDetail;
