import { Fragment } from 'react';
import { useHistory, useLocation  } from 'react-router-dom'; //importing useLocation hook 

import QuoteItem from './QuoteItem';
import classes from './QuoteList.module.css';


const QuoteList = (props) => {

  const history = useHistory();
  const location = useLocation(); // defining useLocation hook 

  console.log(location); // console logging the value of location object that we get from
  // our hook

  const changeSortingHandler = () => { 
    history.push('/quotes?sort=asc');

  };

  return (
    <Fragment>

      <div className={classes.sorting}>
        <button onClick={changeSortingHandler}>Sort Ascending</button>
      </div>

      <ul className={classes.list}>
        {props.quotes.map((quote) => (
          <QuoteItem
            key={quote.id}
            id={quote.id}
            author={quote.author}
            text={quote.text}
          />
        ))}
      </ul>
    </Fragment>
  );
};

export default QuoteList;
