import { useParams, Route } from 'react-router-dom'; 
import { Fragment } from 'react';

import HighlightedQuote from '../components/quotes/HighlightedQuote'; // importing 
// highlighted quote component to use here

import Comments from '../components/comments/Comments'; 

const DUMMY_QUOTES = [ // copied the DUMMY_QUOTES array from AllQuotes component
    {
        id: 'q1',
        author: 'Kartik',
        text: 'Travelling around the would should be your major goal.'
    },
    {
        id: 'q2',
        author: 'Radhika',
        text: 'I am into adventure sports.'
    },
    {
        id: 'q3',
        author: 'Akshat',
        text: 'You should play on probabilities even if it wastes your time.'
    },
    {
        id: 'q4',
        author: 'Ayushi',
        text: 'Mujhse nhi ho payega ye sab, ho gya toh bhi I will ruin everything!'
    },
    {
        id: 'q5',
        author: 'Priyal',
        text: 'Pagal hai utkarsh, I know he wont text me saamne se!'
    }
];

const QuoteDetail = (props) => {

    const params = useParams();

    const data = DUMMY_QUOTES.find(quote => quote.id === params.quoteId); //checking for matching quote
    // in the quotes array, later on we will do this on a backed server rather than a
    // dummy data set.

    if(!data){
        return <p>No Quote Found!</p>
    }

    return (
        <Fragment>
            <HighlightedQuote 
                text = {data.text}
                author = {data.author}
            />

            <Route path={`/quotes/${params.quoteId}/comments`}>
                <Comments />
            </Route>    
        </Fragment>

    );
};

export default QuoteDetail;
