import { Fragment } from 'react';
import { useHistory } from 'react-router-dom'; //importing useHistory hook 

import QuoteItem from './QuoteItem';
import classes from './QuoteList.module.css';


const QuoteList = (props) => {

  const history = useHistory(); // defining the useHistory hook

  const changeSortingHandler = () => { // defining the changeSortingHandler function 
    history.push('/quotes?sort=asc');   // pushing the url to be this after when this
                                          // handler function executes.

  };

  return (
    <Fragment>

{/* adding the button here to sort the list of quotes */}
      <div className={classes.sorting}>
        <button onClick={changeSortingHandler}>Sort Ascending</button>
      </div>

      <ul className={classes.list}>
        {props.quotes.map((quote) => (
          <QuoteItem
            key={quote.id}
            id={quote.id}
            author={quote.author}
            text={quote.text}
          />
        ))}
      </ul>
    </Fragment>
  );
};

export default QuoteList;
