import { useParams, Route, NavLink, useRouteMatch } from 'react-router-dom';
import { Fragment } from 'react';

import HighlightedQuote from '../components/quotes/HighlightedQuote';

import Comments from '../components/comments/Comments'; 

const DUMMY_QUOTES = [ 
    {
        id: 'q1',
        author: 'Kartik',
        text: 'Travelling around the would should be your major goal.'
    },
    {
        id: 'q2',
        author: 'Radhika',
        text: 'I am into adventure sports.'
    },
    {
        id: 'q3',
        author: 'Akshat',
        text: 'You should play on probabilities even if it wastes your time.'
    },
    {
        id: 'q4',
        author: 'Ayushi',
        text: 'Mujhse nhi ho payega ye sab, ho gya toh bhi I will ruin everything!'
    },
    {
        id: 'q5',
        author: 'Priyal',
        text: 'Pagal hai utkarsh, I know he wont text me saamne se!'
    }
];

const QuoteDetail = (props) => {

    const match = useRouteMatch();  
    const params = useParams();

    const data = DUMMY_QUOTES.find(quote => quote.id === params.quoteId); 

    if(!data){
        return <p>No Quote Found!</p>
    }

    return (
        <Fragment>
            <HighlightedQuote 
                text = {data.text}
                author = {data.author}
            />

        <Route path={match.path} exact>
            <div className='centered'>

            {/* using the match object to provide dynamic path instead of the earlier used
            path /quotes/${params.quoteId} */}
                <NavLink className='btn--flat' to={`${match.url}/comments`}>
                    <p>Comment Section</p>
                </NavLink>

            </div>
        </Route>

            {/* using the match object to provide dynamic path instead of the earlier used
            path /quotes/${params.quoteId} */}

            <Route path={`${match.path}/comments`}>
                <Comments />
            </Route>    
        </Fragment>

    );
};

export default QuoteDetail;
